    [Theory]
    [InlineData(SampleData, 13)]
    [InlineData(PuzzleData, 1533)]
    public void Part1(string input, int expected)
    {
        var rows = input.Split('\n');
        var map = new Map();
        for (int y = 0; y < rows.Length; y++)
        {
            for (int x = 0; x < rows[0].Length; x++)
            {
                map.Add(x, y, rows[y][x]);
            }
        }

        Assert.Equal(expected, map.AvailablePapers());

    }

    [Theory]
    [InlineData(SampleData, 43)]
    [InlineData(PuzzleData, 9206)]
    public void Part2(string input, int expected)
    {
        var rows = input.Split('\n');
        var map = new Map();
        for (int y = 0; y < rows.Length; y++)
        {
            for (int x = 0; x < rows[0].Length; x++)
            {
                map.Add(x, y, rows[y][x]);
            }
        }

        Assert.Equal(expected, map.RemoveRecursive());

    }

    public class Map
    {
        private readonly Dictionary<Coordinate,Position> positions = new();
        private readonly Lazy<int> maxRow;
        private readonly Lazy<int> maxColumn;

        public Map()
        {
            maxRow = new Lazy<int>(( )=> positions.Keys.Max(c => c.Row));
            maxColumn = new Lazy<int>(( )=> positions.Keys.Max(c => c.Row));
        }

        public void Add(int column, int row, char c)
        {
            var i = c == '@' ? Item.Paper : Item.Empty;
            var coordinate = new Coordinate(column, row);
            positions[coordinate] = new Position(coordinate, i);
        }

        private record Position(Coordinate Coordinate, Item Item);

        private class Coordinate(
            int column,
            int row)
        {
            public readonly int Column = column;
            public readonly int Row = row;

            public static Coordinate operator +(Coordinate a, Coordinate b)
            {
                return new Coordinate(a.Column + b.Column, a.Row + b.Row);
            }

            public override string ToString()
            {
                return $"{Row} - {Column}";
            }

            public override bool Equals(object? obj)
            {
                if (obj is Coordinate c)
                    return Equals(c);
                return false;
            }

            protected bool Equals(Coordinate other)
            {
                return Column == other.Column && Row == other.Row;
            }

            public override int GetHashCode()
            {
                return HashCode.Combine(Column, Row);
            }
        }

        public int AvailablePapers()
        {
            return positions.Values.AsParallel()
                .Where(pos => pos.Item == Item.Paper)
                .Count(pos => IsAvailable(pos, this));
        }

        private bool IsAvailable(Position arg, Map map)
        {
            var neighbours =
            new Coordinate[]{
                new(-1, -1),
                new(-1, 0),
                new(-1, 1),
                new(0, -1),
                new(0, 1),
                new(1, -1),
                new(1, 0),
                new(1, 1),
            };

            var count = neighbours.Select(n => arg.Coordinate + n)
                .Where(map.IsValid)
                .Select(c => positions[c])
                .Count(p => p.Item == Item.Paper);

            return count < 4;
        }

        private bool IsValid(Coordinate coordinate)
        {
            if (coordinate.Row < 0 || coordinate.Row > maxRow.Value)
                return false;

            return coordinate.Column >= 0 && coordinate.Column <= maxColumn.Value;
        }

        public int RemoveRecursive()
        {
            var count = 0;
            while (true)
            {
                var available = positions.AsParallel()
                    .Select(pair => pair.Value)
                    .Where(pos => pos.Item == Item.Paper)
                    .Where(pos => IsAvailable(pos, this))
                    .ToList();

                count += available.Count;
                foreach (var pos in available)
                {
                    positions[pos.Coordinate] = pos with { Item = Item.Empty };
                }

                if (available.Count == 0)
                    break;
            }

            return count;
        }
    }

    public enum Item
